mapper.select:
select count(*) from member_tb
where member_id = #{member_id}
and member_pw = #{member_pw}

mapper.insert:
INSERT INTO file_tb
	(
	file_name,
	file_path
	)
	VALUES
	(
	#{file_name},
	#{file_path}
	);

mapper.select:
SELECT * FROM file_tb
WHERE 
	file_name = #{file_name}
AND
	file_path = #{file_path}
limit 1
